if(change_color){var header=document.querySelector("nav"),orange="#ffa500f7";header.style.background="transparent",window.addEventListener("scroll",function(){window.scrollY>=200?header.style.background=orange:header.style.background="transparent"})}var timer,end=new Date("04/25/2018 0:0 AM"),_second=1e3,_minute=60*_second,_hour=60*_minute,_day=24*_hour;function showRemaining(){var e=new Date,n=end-e;if(n<0)return clearInterval(timer),void(document.getElementById("countdown").innerHTML="EXPIRED!");var r=Math.floor(n/_day),o=Math.floor(n%_day/_hour),t=Math.floor(n%_hour/_minute),a=Math.floor(n%_minute/_second);document.querySelector(".days p").innerHTML=r,document.querySelector(".hours p").innerHTML=o,document.querySelector(".minutes p").innerHTML=t,document.querySelector(".seconds p").innerHTML=a}timer=setInterval(showRemaining,1e3);